INSERT INTO `ajuda` (`id`, `indice`, `mapeamento`, `titulo`, `conteudo`, `permissao`) VALUES
(1, 'Usuário/Login', 'login', 'Login', '<p>Ao acessar o <span class="nomeSivacAjuda">Sivac</span> é necessário selecionar o estado em que está localizado a sua unidade de saúde e clicar no botão  <span class="campoFormulario">prosseguir</span> para efetuar o login no sistema. </p>\r\n<p align="center">&nbsp;</p>\r\n<p align="center"><img src="../Imagens/Ajuda/login01.jpg" alt="Login início" /></p>\r\n<p>Em seguida o usuário do <span class="nomeSivacAjuda">Sivac</span> deve  digitar o login e a senha e clicar no botão <span class="campoFormulario">entrar</span>. </p>\r\n<p>&nbsp;</p>\r\n<p align="center"><img src="../Imagens/Ajuda/login02.jpg" alt="login" /></p>\r\n<p align="center">&nbsp;</p>\r\n<p align="center"><img src="../Imagens/Ajuda/login03.jpg" alt="login2" /></p>\r\n', 'LIBERADO'),
(2, 'Administração/Unidade/Inserir', 'Adm/inserirUnidadeDeSaude ', 'Inserir unidade de saúde', '<p>Formulário para o cadastro da  unidade de saúde¹ correspondente, constituído dos campos nome, CNES (Cadastro  Nacional de Estabelecimento de Saúde – identificação da unidade de  saúde, que é única em todo   Brasil e determinada pelo Ministério da Saúde), cidade, bairro,  logradouro, telefone e cep.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/adicionarUnidade.jpg" alt="Adicionar Unidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote> ¹ Uma unidade de saúde só poderá ser  cadastrada por um administrador de acesso master.\r\n</blockquote>\r\n</p>\r\n', 'UNIDADES_INSERIR'),
(3, 'Administração/Unidade/Listar', 'Adm/listarUnidades, Adm/editarUnidadeDeSaude, Adm/excluirUnidadeDeSaude', 'Listar unidades de saúde', '<p>Permite listar¹ as unidades  cadastradas, com a opção de editar ou excluir² através das ações  correspondentes, detalhadas na legenda abaixo. A ação de alteração da unidade  permite ao usuário modificar os dados da unidade através de uma tela com o  formulário correspondente. A ação de exclusão faz abrir uma tela para a  confirmação da exclusão desta unidade.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/listarUnidades.jpg" alt="Listar Unidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ O menu para listar e excluir as unidades  só será exibido ao administrador de acesso master.</blockquote>\r\n<blockquote>² Ao administrador de acesso  padrão será exibido o menu <span class="campoFormulario">alterar</span> diretamente, que permitirá ao  mesmo a alteração de sua unidade, não exibindo a lista de unidades de saúde</blockquote>\r\n</p>\r\n', 'UNIDADES_LISTAR'),
(4, 'Administração/Unidade/Estoque', 'Adm/estoqueDaUnidade', 'Estoque de vacinas nas unidades de saúde', '<p>O formulário tem o objetivo de  alimentar o estoque da unidade de saúde. Para isso, selecione a unidade  correspondente (caso seja administrador master), a vacina e a quantidade a ser  incrementada e escolha <span class="campoFormulario">confirmar</span>. Caso tenha inserido a quantidade  errada, escolha imediatamente <span class="campoFormulario">estornar </span>para voltar ao estoque  anterior. O estorno removerá somente a quantidade inserida da operação  anterior. Não é possível estornar vacinas adicionadas anteriormente. A lista  abaixo consiste no nome vacina, quantidade em estoque e unidade de saúde em que  o estoque foi alocado.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/estoqueVacina.jpg" alt="Adicionar Estoque" /></p>\r\n', 'UNIDADES_ESTOQUE'),
(5, 'Administração/ACS/Inserir', 'Adm/inserirAcs', 'Inserir ACS', '<p>O formulário contém a descrição dos  dados para o cadastro de um agente comunitário de saúde (ACS). Os campos que  possuem o símbolo * (asterisco) precisam ser preenchidos, pois são  obrigatórios. Ao terminar de preencher o formulário, utilize o botão  <span class="campoFormulario">inserir</span> para confirmar o cadastro, e caso queira apagar os dados  digitados para preencher novamente o formulário, basta utilizar o botão  <span class="campoFormulario">limpar</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/adicionarAcs.jpg" alt="Inserir ACS" /></p>', 'ACS_INSERIR'),
(6, 'Administração/ACS/Listar', 'Adm/listarAcs, Adm/editarAcs, Adm/excluirAcs', 'Listar ACS', '<p>Permite listar os agentes comunitários  de saúde (ACS) cadastrados, com a opção de <span class="campoFormulario">alterar dados</span>¹ ou <span class="campoFormulario">excluir</span>². </p>\r\n<p align="center"><img src="../Imagens/Ajuda/listarAcs.jpg" alt="Inserir ACS" /></p>\r\n<p>A ação de <span class="campoFormulario">alterar dados</span> permite modificar  os dados do cadastro através de uma tela com o formulário correspondente. Caso  queira apagar os dados digitados para preencher novamente, basta utilizar o  botão <span class="campoFormulario">limpar</span>. Após preencher os campos solicitados no formulário  basta <span class="campoFormulario">confirmar </span>para validar as informações.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/alterarAcs.jpg" alt="altera acs" /></p>\r\n<p>A ação de exclusão faz abrir uma  tela para a confirmação da exclusão deste ACS.</p>\r\n<p align="center">&nbsp;</p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>\r\n  <div align="justify">¹ O menu <span class="campoFormulario">listar ACS </span>será  exibido ao administrador de acesso master e padrão. Ao administrador de acesso  padrão será exibido somente a opção <span class="campoFormulario">alterar</span>, que permitirá a modificação  dos dados de cadastro.</div>\r\n</blockquote>\r\n<div align="justify">\r\n  <blockquote>² A opção  <span class="campoFormulario">excluir  ACS</span> só  será permitida ao administrador de acesso master.</blockquote>\r\n</div>\r\n', 'ACS_LISTAR'),
(7, 'Administração/Campanha/Inserir', 'Adm/inserirCampanha, Adm/inserirVacinasNaCampanha,', 'Inserir Campanha', '<p>O formulário tem o objetivo de  cadastrar uma nova campanha de vacinação.  </p>\r\n<p>Para criar¹ a nova campanha  preencha o formulário, o campo do texto complementar (opcional) e utilize o  botão <span class="campoFormulario">avançar</span>. \r\n<p align="center"><img src="../Imagens/Ajuda/adicionarCampanha.jpg" alt="Inserir Campanhas" /></p>\r\n<p>Na próxima página selecione a(s) vacina(s) integrante(s) da  campanha. Caso queira desfazer a escolha da(s) vacina(s) clique em  <span class="campoFormulario">desmarcar</span>. Para cadastrar a vacina utilize o botão <span class="campoFormulario">adicionar</span>. </p>\r\n<p>Antes de finalizar o processo de  criação da nova campanha, na coluna características utilize o botão <span class="campoFormulario">listar</span>  e/ ou <span class="campoFormulario">adicionar</span>² para acrescentar as características dos indivíduos que serão  incluídos na faixa vacinável da população.</p>\r\n<p>O botão <span class="campoFormulario">detalhes</span> ao ser clicado  exibe, somente para leitura, as características da vacina. Com estas etapas  concluídas utilize o botão <span class="campoFormulario">finalizar</span> para cadastrar a campanha.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/adicionarCampanha3.jpg" alt="Inserir Campanhas" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ Somente o administrador de  acesso master está autorizado adicionar nova campanha.</blockquote>\r\n<blockquote>² O <span class="nomeSivacAjuda">Sivac</span> disponibiliza as  características padrão da vacina determinadas pelo Ministério da Saúde.  Portanto antes de adicionar as características à campanha realize a leitura  detalhada das características padrão vacina.\r\n</blockquote>\r\n    </p>', 'CAMPANHAS_INSERIR'),
(35, 'Indivíduo/Caderneta', 'Adm/vacinarRetroativo, Adm/listarPessoasVacinaveis, Adm/vacinar', 'Atualizar caderneta', '<p>Esta opção permite registrar a  aplicação de vacinas que ocorreram em uma data posterior à atual. Para  iniciar o registro da vacinação proceda de forma padrão.Insira os dados e utilize <span class="campoFormulario">buscar</span> para acessar as informações do indivíduo.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/atualizarCaderneta.jpg" alt="atualizarCaderneta " /></p>\r\n<p>Para confirmar o registro da vacinação¹ é preciso que seja digitado a data em que realmente  ocorreu a vacinação.Para prosseguir com a vacinação, clique no botão  <span class="campoFormulario">confirmar</span>.</p>\r\n<p>Você poderá opcionalmente descontar esta dose do seu estoque (caso a data seja recente e usou realmente uma dose da vacina estocada em sua unidade). Proceda  com atenção ao atualizar a caderneta e prefira sempre a vacinação convencional.</p>\r\n<p>Caso tenha dúvidas sobre a situação  vacinal do indivíduo confira a legenda no rodapé da página antes de vacinar.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/atualizarCaderneta2.jpg" alt="atualizarCaderneta2" /></p>\r\n<p align="center">&nbsp;</p>\r\n<p><strong>Nota:</strong>\r\n<blockquote> ¹ Verifique a data de nascimento  antes da vacinação. </blockquote>\r\n</p>\r\n', 'INDIVIDUOS_CADERNETA'),
(40, 'Relatório/Campanha/Indivíduos vacinados por campanha e unidade', 'Rel/exibirFormularioEscolherRelatorio&tipo=PessoasVacinadasPorCampanhaEUnidade&tituloRelatorio=Vacinados+por+Campanha+e+Unidade', 'Indivíduos vacinados por campanha e unidade', '<p>Para gerar o relatório selecione a unidade de saúde e campanha e utilize o botão <span class="campoFormulario">confirmar</span>.</p> \r\n<p align="center"><img src="../Imagens/Ajuda/vacinadosPorCampanhaUnidade.jpg" alt="Vacinar com data retroativa" /></p>\r\n<p>Uma nova página será exibida com a lista de vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/vacinadosPorCampanhaUnidade3.jpg" alt="Vacinar com data retroativa" /></p>', 'RELATORIOS_CAMPANHA'),
(8, 'Administração/Campanha/Listar', 'Adm/listarCampanhas, Adm/editarCampanha, Adm/visualizarCampanha, Adm/excluirCampanha', 'Listar campanhas', '<p>Permite listar¹ as campanhas  cadastradas, com a opção de <span class="campoFormulario">editar</span>² ou <span class="campoFormulario">excluir</span>². </p>\r\n<p>Ao clicar em <span class="campoFormulario">editar uma campanha</span>,  um formulário com os respectivos dados desta será exibido. É possível, no  formulário, alterar o nome e a data de início e de finalização da campanha,  cadastrar e excluir as vacinas que farão parte da campanha, listar  características e exibir detalhes da(s) vacina(s) e por fim adicionar um texto complementar  para ser exibido quando clicar no botão <span class="campoFormulario">visualizar</span> de uma campanha.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/listarCampanha.jpg" alt="Listar Campanhas" /></p>\r\n<p><strong>Nota:</strong></p>\r\n<blockquote>¹ Ao usuário padrão só é permitido a  visualização da campanha  através do campo ações no botão  <span class="campoFormulario">visualizar campanha</span>.</blockquote>\r\n<blockquote>² Ao administrador de acesso master, no  campo ações, é permitido <span class="campoFormulario">editar</span>, <span class="campoFormulario">excluir</span> e <span class="campoFormulario">visualizar campanhas</span>.</blockquote>\r\n</p>\r\n', 'CAMPANHAS_LISTAR'),
(9, 'Administração/Texto Inicial/Configurar', 'Adm/editarInicio', 'Configurar Texto Inicial', '<p>O texto exibido na tela inicial do <span class="nomeSivacAjuda">Sivac</span>  pode ser modificado¹. Para alterá-lo utilize o botão <span class="campoFormulario">limpar</span> para redigir o  novo texto e o botão <span class="campoFormulario">confirmar</span> para salvar a modificação.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/textoInicial.jpg" alt="Configurar Texto Inicial" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ Somente o administrador de  acesso master pode alterar o texto inicial.</blockquote>\r\n<blockquote>O texto pode conter marcações HTML simples (como <span class="campoFormulario">&lt;br /&gt;</span> ou <span class="campoFormulario">&lt;p&gt;&lt;/p&gt;</span>) para deixar um trecho ou todo o  texto formatado.</blockquote>\r\n</p>\r\n', 'CONFIGURAR_TEXTOINICIAL'),
(10, 'Indivíduo/Buscar', 'Adm/listarPessoa, Adm/editarPessoa, Adm/excluirPessoa', 'Buscar Indivíduo', '<p>O formulário tem o objetivo de  localizar o cadastro do indivíduo que será vacinado. Ao terminar de preencher o  formulário verifique a cidade e o estado selecionado e utilize o botão  <span class="campoFormulario">buscar</span> para iniciar a busca.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/buscarIndividuo.jpg" alt="Localizar indivíduo" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O <span class="nomeSivacAjuda">Sivac</span> automaticamente seleciona o  seu município para realizar as buscas, caso queira buscar o indivíduo em outro estado  ou cidade desmarque a opção que seleciona o seu município e escolha o um novo estado  e cidade.\r\n</blockquote></p>\r\n', 'INDIVIDUOS_BUSCAR'),
(11, 'Indivíduo/Cadastrar ', 'Adm/inserirPessoa', 'Cadastrar  Indivíduo', '<p>O formulário tem o objetivo de  cadastrar o novo indivíduo que será vacinado. Os campos que possuem o símbolo *  (asterisco) precisam ser preenchidos, pois são obrigatórios. Ao terminar de preencher  o formulário, utilize o botão <span class="campoFormulario">confirmar</span> para que o cadastro seja feito;   caso queira apagar os dados digitados para preencher novamente o formulário,  basta utilizar o botão <span class="campoFormulario">limpar</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/adicionarIndividuo.jpg" alt="Cadastrar Indivíduo" /></p>\r\n', 'INDIVIDUOS_CADASTRAR'),
(12, 'Indivíduo/Vacinar', 'Adm/listarPessoasVacinaveis, Adm/vacinar, Adm/vacinarRetroativo', 'Vacinar', '<p>O formulário tem o objetivo de registrar  a vacinação do indivíduo na ocorrência de campanhas ou vacinações de rotina.Para iniciar o registro de vacinação do indivíduo, selecione campanha, vacina e preencha o campo nome e opcionalmente data de nascimento ou CPF.</p>\r\nApós o preenchimento dos critérios, selecione  opcionalmente o estado e a cidade em que o indivíduo é morador e utilize o botão <span class="campoFormulario">buscar</span> para iniciar a busca.\r\n<p align="center"><img src="../Imagens/Ajuda/individuoVacinar.jpg" alt="Indivíduo Vacinar" /></p>\r\nTambém é possível listar somente os indivíduos com doses em atraso, marcando a caixa com esta opção.Clicando sobre o nome do indivíduo², você será direcionado para a tela de vacinação.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/individuoVacinar2.jpg" alt="Indivíduo Vacinar" /></p>\r\n\r\n <p>Para confirmar o registro da vacina¹ clique no botão aplicar, em forma de seringa. \r\nCaso tenha dúvidas sobre a situação vacinal do indivíduo confira a legenda no rodapé da página antes de vacinar.\r\n\r\n<p align="center"><img src="../Imagens/Ajuda/individuoVacinar3.jpg" alt="Indivíduo Vacinar" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ Verifique a data de nascimento  antes da vacinação.\r\n</blockquote>\r\n<blockquote>² Indivíduos que não tomaram nenhuma dose se apresentam em vermelho, os que iniciaram o ciclo estão em azul e os que já completaram o ciclo estão em verde.\r\n</blockquote>\r\n', 'INDIVIDUOS_VACINAR'),
(13, 'Intercorrências', 'Adm/intercorrencias, Adm/intercorrenciasAdicionadas', 'Intercorrências ', '<p>O formulário tem a função de  registrar o indivíduo que apresentou o evento adverso após a aplicação da  vacina¹. Para registrar a(s) intercorrência(s) preencha os campos <span class="campoFormulario">nome</span>\r\n,<span class="campoFormulario">vacina</span>, <span class="campoFormulario">evento adverso</span> e utilize  o botão <span  e utilize  o botão <span  e utilize  o botão <span class="campoFormulario">buscar</span> para encontrar o indivíduo que foi vacinado. O <span class="nomeSivacAjuda">Sivac</span> listará  os indivíduos localizados. \r\n<p align="center"><img src="../Imagens/Ajuda/intercorrencia.jpg" alt="Intercorrência" /></p>\r\n<p>Para adicionar o evento adverso preencha <span class="campoFormulario">Data inicial do evento ocorrido</span>, <span class="campoFormulario">hora</span> e utilize  o botão <span  e utilize  o botão <span , selecione a vacina, insira a data e o horário e utilize  o botão <span class="campoFormulario">confirmar</span> para concluir o registro.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/intercorrencia2.jpg" alt="Intercorrência" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ O administrador de acesso padrão  também é apto a realizar esta operação\r\n</blockquote>\r\n	</p>', 'INDIVIDUOS_INTERCORRENCIA'),
(14, 'Relatório/Cidade/Vacinados', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosVacinadosPorCidade&tituloRelatorio=Vacinados+por+cidade', 'Indivíduos vacinados por cidade', '<p>O relatório tem a finalidade de listar  os indivíduos vacinados dentro de um determinado estado e cidade por vacina  aplicada. Para gerar o relatório selecione o estado, a cidade, a vacina e utilize  o botão <span class="campoFormulario">confirmar</span> para gerar. Em seguida uma nova página será exibida com a  lista de vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioVacinadoCidade.jpg" alt="Vacinados por cidade" /></p>\r\n<p><strong>Nota:</strong>\r\n\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n   	</p>', 'RELATORIOS_CIDADE'),
(15, 'Relatório/Cidade/Vacinados por período', 'Rel/exibirFormularioEscolherRelatorio&tipo=ComVacinaPorCidade&tituloRelatorio=Vacinados+por+período+e+cidade', 'Indivíduos vacinados por período e por cidade', '<p>O relatório tem a finalidade de listar  os indivíduos¹ vacinados dentro de um determinado estado e cidade, por vacina  aplicada e em um determinado período. Para gerar o relatório selecione o estado,  a cidade, a vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span> para gerar.  Em seguida uma nova página será exibida com a lista de vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioVacinadoPeriodoCidade.jpg" alt="Vacinados por períodos e por cidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_CIDADE'),
(16, 'Relatório/Cidade/Não vacinados', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosNaoVacinadosPorCidade&tituloRelatorio=Não+vacinados+por+cidade', 'Indivíduos não vacinados por cidade', '<p>O relatório tem a finalidade de listar  os indivíduos¹ não vacinados dentro de um determinado estado e cidade por vacina.  Para gerar o relatório selecione o estado, a cidade, a vacina e utilize o botão  <span class="campoFormulario">confirmar</span>. Em seguida uma nova página será exibida com a lista de não  vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioNaoVacinadoCidade.jpg" alt="Não vacinados por cidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_CIDADE'),
(17, 'Relatório/Cidade/Não vacinados por período', 'Rel/exibirFormularioEscolherRelatorio&tipo=SemVacinaPorCidade&tituloRelatorio=Não+vacinados+por+período+e+cidade ', 'Indivíduos não vacinados por período e Cidade', '<p>O relatório tem a finalidade de listar  os indivíduos¹ não vacinados dentro de um determinado estado, cidade, por vacina ou um determinado período. Para gerar o relatório selecione o estado, a cidade, a  vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span> para gerar. Em seguida  uma nova página será exibida com a lista de não vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioNaoVacinadoPeriodoCidade.jpg" alt="Não vacinados por período e cidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n</p>', 'RELATORIOS_CIDADE'),
(18, 'Relatório/Cidade/Com doses a vencer', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosComDosesVencerPorCidade&tituloRelatorio=Indivíduos+com+doses+a+vencer+por+cidade', 'Indivíduos com doses a vencer por período e por cidade', '<p>O relatório tem a finalidade de listar  os indivíduos¹ que iniciaram o ciclo de vacinação e não concluíram. É gerado por  vacina aplicada, por estado e cidade e o período de retorno do indivíduo a unidade de saúde para aplicação de nova dose.Para gerar o relatório selecione o estado,  a cidade e a vacina.Informe o período e utilize o botão <span class="campoFormulario">confirmar</span> para  gerar uma nova página com a lista.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/RelDosesVencPerCid.jpg" /></p>\r\n<p align="left">Ao clicar em algum  dos individuos, será exibida uma nova tela com o relatório das vacinas com doses a vencer para o indivíduo escolhido.</p>\r\n<p align="center">&nbsp;</p>\r\n<p align="center"><img src="../Imagens/Ajuda/RelDosesVencPerCid2.jpg" alt="Relatório doses vencer indivíduo" </p>\r\n<p>Esta nova página exibi todas as doses pendentes e também permiti efetuar a aplicação. Para efetuar a aplicação basta utilizar <span class="campoFormulario"> aplicar </span>. Caso contrário basta utilizar <span class="campoFormulario">voltar</span>.</p>   \r\n<p><p align="center"><img src="../Imagens/Ajuda/RelDosesVencPerCid4.jpg" alt="Relatório doses vencer indivíduo" </p>\r\n<p align="center">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_CIDADE'),
(19, 'Relatório/Indivíduo/Caderneta de vacinação', 'Rel/exibirFormularioEscolherRelatorio&tipo=CriarCadernetaDeVacinacao&tituloRelatorio=Caderneta+de+vacinação', 'Caderneta de vacina', '<p>O relatório tem a finalidade de listar,  através do nome do indivíduo, as vacinas com as suas respectivas datas de  aplicação, as vacinas que podem ser aplicadas e a data de retorno e ideais para  aplicação de nova(s) dose(s). </p>\r\n<p>Para gerar o relatório informe o  nome do indivíduo no formulário e utilize o botão <span class="campoFormulario">buscar</span> para encontrá-lo. O <span class="nomeSivacAjuda">Sivac</span>  listará os indivíduos. Clique sobre o nome que deseja  visualizar a caderneta e em seguida uma nova página será exibida com as  informações. </p>\r\n<p align="center"><img src="../Imagens/Ajuda/caderneta.jpg" alt="Caderneta de Vacina" /></p>\r\nAgora escolha as vacinas e se as observações serão exibidas na caderneta marcando as caixas referentes. Para visualizar a caderneta basta apertar  <span class="campoFormulario">confirmar</span>.\r\n<p align="center"><img src="../Imagens/Ajuda/vacinasparaacaderneta.jpg" alt="Caderneta de Vacina 2" /></p>\r\n<p>Segue a caderneta com as observações referentes à vacina.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/caderneta1.jpg" alt="Caderneta de Vacina 2" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_INDIVIDUO'),
(20, 'Relatório/Indivíduo/Vacinas por indivíduo', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarVacinasPorUsuario&tituloRelatorio=Vacinas+por+indivíduo', 'Vacinas por  Indivíduo', '<p>O relatório tem a finalidade de listar,  através do nome do indivíduo, a(s) vacina(s) e a quantidade de dose(s)  aplicadas com as suas respectivas datas de vacinação. </p>\r\n<p>Para gerar o relatório informe o  nome do indivíduo no formulário e utilize o botão <span class="campoFormulario">buscar</span> para encontrá-lo. Clique  sobre o nome que deseja visualizar e o <span class="nomeSivacAjuda">Sivac</span> exibirá em seguida uma nova página  com as informações das vacinas aplicadas. </p>\r\n<p align="center"><img src="../Imagens/Ajuda/vacinaIndividuo.jpg" alt="Vacina por individuo" width="601" height="375" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_INDIVIDUO'),
(21, 'Relatório/Unidade/Vacinados', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosVacinadosPorUnidade&tituloRelatorio=Vacinados+por+unidade', 'Indivíduos vacinados por unidade', '<p>O relatório tem a finalidade de listar  os indivíduos vacinados em cada unidade de saúde por vacina aplicada. Para gerar  o relatório selecione a unidade de saúde, a vacina e utilize o botão  <span class="campoFormulario">confirmar</span>. Em seguida uma nova página será exibida com a lista de  vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioVacinadoUnidadejpg.jpg" alt="Vacinado por Unidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_UNIDADE'),
(22, 'Relatório/Unidade/Vacinados por período', 'Rel/exibirFormularioEscolherRelatorio&tipo=ComVacinaPorUnidade&tituloRelatorio=Vacinados+por+período+e+unidade', 'Vacinados por período e unidade', '<p>O relatório tem a finalidade de listar  os indivíduos vacinados em cada unidade de saúde por vacina aplicada e em um  determinado período. Para gerar o relatório selecione a unidade de saúde e a  vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span> para gerar. Em seguida  uma nova página será exibida com a lista de vacinados.</p>\r\n\r\n<p align="center"><img src="../Imagens/Ajuda/vacinasperiodounidade.jpg\r\n"alt="Vacinados por periodo e unidade" /></p>\r\n\r\n<p>&nbsp;</p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n     </p>\r\n\r\n', 'RELATORIOS_UNIDADE'),
(23, 'Relatório/Unidade/Não vacinados', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosNaoVacinadosPorUnidade&tituloRelatorio=Não+vacinados+por+unidade', 'Não vacinados por unidade', '<p>O relatório tem a finalidade de listar  os indivíduos não vacinados em cada unidade de saúde por vacina. Para gerar o  relatório selecione a unidade de saúde e a vacina e utilize o botão <span class="campoFormulario">confirmar</span>. Em seguida uma nova página será exibida com a lista de não  vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioNaoVacinadoUnidade.jpg" alt="Não vacinados por unidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_UNIDADE'),
(24, 'Relatório/Unidade/Não vacinado por período', 'Rel/exibirFormularioEscolherRelatorio&tipo=SemVacinaPorUnidade&tituloRelatorio=Não+vacinados+por+período+e+unidade', 'Não vacinados por período e unidade', '<p>O relatório tem a finalidade de listar  os indivíduos não vacinados em cada unidade de saúde por vacina e em um  determinado período. Para gerar o relatório selecione a unidade de saúde e a  vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span>. Em seguida  uma nova página será exibida com a lista de não vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioNaoVacinadoPeriodoUnidade.jpg" alt="Não vacinados por período e unidade" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n   	</p>', 'RELATORIOS_UNIDADE'),
(25, 'Relatório/Unidade/Com doses a vencer por unidades', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosComDosesVencerPorUnidade&tituloRelatorio=Com+doses+a+vencer+por+unidade', 'Vacinas com doses a vencer por unidade', '<p>O relatório tem a finalidade de listar  os indivíduos que iniciaram o ciclo de vacinação e não concluíram. É gerado por  vacina aplicada, por unidade de saúde e o período de retorno do indivíduo para  aplicação de nova dose. </p>\r\n<p>Para gerar o relatório selecione a  unidade de saúde e a vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span>. Em seguida uma nova página será exibida com a lista.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/RelUnidDosVencer.jpg" alt="Vacinar com data retroativa" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>\r\n  <div align="justify">O administrador de acesso padrão  também é apto a realizar esta operação.\r\n  </div>\r\n</blockquote>\r\n	</p>', 'RELATORIOS_UNIDADE'),
(26, 'Relatório/ACS/Indivíduos vacinados', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosVacinadosPorAgente&tituloRelatorio=Vacinados+por+ACS', 'Indivíduos vacinados por agente comunitário de saúde', '<p>O relatório tem a finalidade de listar  os indivíduos vacinados por determinado Agente Comunitário de Saúde e por  vacina aplicada. Para gerar o relatório selecione o ACS e a vacina e utilize o  botão <span class="campoFormulario">confirmar</span> para gerar. Em seguida uma nova página será exibida com a  lista de vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioVacinadoAcs.jpg" alt="Vacinados por ACS" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_ACS'),
(27, 'Relatório/ACS/Vacinados por período', 'Rel/exibirFormularioEscolherRelatorio&tipo=ComVacinaPorAcs&tituloRelatorio=Vacinados+por+período+e+ACS', 'Indivíduos vacinados por período e agente comunitário de saúde', '<p>O relatório tem a finalidade de listar  os indivíduos vacinados por determinado Agente Comunitário de Saúde, por vacina  aplicada e em um determinado período. Para gerar o relatório selecione o ACS e  a vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span> para gerar. Em  seguida uma nova página será exibida com a lista de vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioVacinadoPeriodoAcs.jpg" alt="Vacinado por período e ACS" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n    </p>', 'RELATORIOS_ACS'),
(28, 'Relatório/ACS/Indivíduos não vacinados', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosNaoVacinadosPorAgente&tituloRelatorio=Não+vacinados+por+ACS', 'Indivíduos não vacinados por agente comunitário de saúde', '<p>O relatório tem a finalidade de listar  os indivíduos não vacinados por determinado Agente Comunitário e por vacina.  Para gerar o relatório selecione o ACS e a vacina e utilize o botão <span class="campoFormulario">confirmar</span>  para gerar. Em seguida uma nova página será exibida com a lista de não  vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioNaoVacinadoAcs.jpg" alt="Não vacinados por ACS" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_ACS'),
(29, 'Relatório/ACS/Não vacinado por período', 'Rel/exibirFormularioEscolherRelatorio&tipo=SemVacinaPorAcs&tituloRelatorio=Não+vacinados+por+período+e+ACS', 'Indivíduos não vacinados por período e por agente comunitário de saúde', '<p>O relatório tem a finalidade de listar  os indivíduos não vacinados por determinado Agente Comunitário, por vacina e em  um determinado período. Para gerar o relatório selecione o ACS e a vacina,  informe o período e utilize o botão <span class="campoFormulario">confirmar</span> para gerar. Em seguida uma nova  página será exibida com a lista de não vacinados.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioNaoVacinadoPeriodoAcs.jpg" alt="Não vacinados por período e ACM" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>O administrador de acesso padrão  também é apto a realizar esta operação.\r\n</blockquote>\r\n	</p>', 'RELATORIOS_ACS'),
(30, 'Relatório/ACS/Com doses a vencer', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosComDosesVencerPorAcs&tituloRelatorio=Com+doses+a+vencer+por+ACS', 'Indivíduos com dose a vencer por agente comunitário de saúde', '<p>O relatório tem a finalidade de listar  os indivíduos que iniciaram o ciclo de vacinação e não concluíram. É gerado por  vacina aplicada, por Agente Comunitário e o período para aplicação da nova  dose.</p>\r\n<p>Para gerar o relatório selecione o  Agente Comunitário e a vacina, informe o período e utilize o botão <span class="campoFormulario">confirmar</span>  .Em seguida uma nova página será exibida com a lista.</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relatorioDoseVencerPeriodoAcs.jpg" alt="dose vencer acs" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>\r\n  <div align="justify">O administrador de acesso padrão  também é apto a realizar esta operação.\r\n  </div>\r\n</blockquote>\r\n	</p>', 'RELATORIOS_ACS'),
(31, 'Usuário/Usuário conectado', 'login', 'Usuário conectado', '<p>Após o usuário <span class="nomeSivacAjuda">Sivac</span> se conectar para acessar o sistema, no canto inferior esquerdo ficará indicada a identificação do usuário   e a base de dados¹ que está sendo acessada no momento.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/logado.jpg" alt="Logado" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>\r\n  <div align="left">¹ Somente o administrador de  acesso master poderá escolher a base de dados. O administrador padrão só terá acesso à base na qual se encontra o seu cadastro. Ao clicar sobre a identificação de usuário do <span class="nomeSivacAjuda">Sivac</span>, é possível alterar os dados do cadastro.</div>\r\n</blockquote>\r\n</p>', 'LIBERADO'),
(32, 'Usuário/Desconectar', 'login', 'Desconectar usuário', '<p>Utilize o botão <span class="campoFormulario">desconectar</span> para  sair do <span class="nomeSivacAjuda">Sivac</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/desconectar.jpg" alt="Desconectar" /></p>\r\n<h3 align="center">Dúvida</h3>\r\n<p>Utilize o símbolo <span class="campoFormulario">?</span>  (interrogação) em caso de dúvidas sobre as funções e os recursos do Sistema.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/interrogacao.jpg" alt="Dúvida" /></p>\r\n<p>&nbsp;</p>', 'LIBERADO'),
(33, 'Contato', 'contato', 'Contato', '<p>Utilize o formulário para entrar em  contato com a equipe de suporte e desenvolvimento do <span class="nomeSivacAjuda">Sivac</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/contato.jpg" alt="Contato" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>\r\nContato é a única sessão que não  necessita de identificação.\r\n</blockquote></p>', 'LIBERADO'),
(34, ' Introdução', 'intro', 'Introdução ao Sivac', '<p>O Sistema informatizado de vacinação — <span class="nomeSivacAjuda"><span class="nomeSivacAjuda">Sivac</span></span> — é uma  ferramenta de auxilio aos profissionais do Sistema Único de Saúde para  processar de forma integrada e online o registro e monitoramento, individual e  coletivo, da aplicação de vacinas no país.</p>\r\n<p>O <span class="nomeSivacAjuda">Sivac</span> foi criado para suprir a necessidade da monitoração do sitema vacinal brasileiro de forma coerente e precisa.</p>\r\n<p>	 Através do  sistema é possível:</p>\r\n<ul type="disc">\r\n	<li>Ter       uma estatística exata que poderá ser baseada na unidade, ACS, cidade, campanha,       vacina, períodos, etc;</li>\r\n	<li>Registrar       rapidamente a vacinação da população do município;</li>\r\n	<li>Ter       uma ligação direta entre dados estatísticos e a pessoa, podendo       rapidamente vacinar ou contatar a mesma;</li>\r\n	<li>Possuir       dados não apenas dos vacinados, mas das pessoas que também não foram       vacinadas no município;</li>\r\n	<li>Localizar       e contar o indivíduo que a tempos atrás, tomou determinada dose, em uma       determinada data, aplicada por um determinado vacinador em uma determinada       unidade de saúde.</li>\r\n	<li>Saber       quais são todas as pessoas que tomaram a primeira dose, mas não       completaram o ciclo para a vacina indicada;</li>\r\n	<li>Ter       um controle específico das doses ideais e reais aplicadas;</li>\r\n	<li>Cadastrar       novos agentes, campanhas, unidades, gerenciar o estoque de vacinas das       mesmas;</li>\r\n	<li>Gerenciar       exceções e características específicas de vacinas em campanhas (baseadas,       p.ex. em surtos ou etnias de populações em regiões específicas);</li>\r\n	<li>Tratar       os eventos adversos que podem ocorrer após a vacinação;</li>\r\n	<li>E muito mais...</li>\r\n</ul>\r\n<p>O sistema se encontra previamente povoado com todas as vacinas  existentes, além de todos os municípios do Brasil e também sua população</p>\r\n<p>. Pessoas que ainda não estão no sistema poderão ser cadastradas e os  dados poderão ser atualizados a qualquer momento.</p>\r\n<p>O  <span class="nomeSivacAjuda">Sivac</span> poderá ser configurado para suportar modificações em características  específicas de cada vacina (idade, sexo, etnia, estado), permitindo que se  adéque a surtos ou epidemias de doença cuja vacinação esteja incluída no PNI.  Atendendo a necessidade do município ou estado, podem ser adotadas medidas de  controle que incluem a vacinação em massa da população-alvo.<u></u></p>\r\n<p>O  objetivo principal do <span class="nomeSivacAjuda">Sivac</span> é proporcionar, com o tempo, um mecanismo confiável  de registro e monitoramento do sistema vacinal brasileiro através de dados  reais de vacinação. O sistema poderá auxiliar a tomada de decisão dos gestores acerca  do controle de doenças imunopreveníveis através de amplas coberturas vacinais,  para que a população possa ser provida de adequada proteção imunitária, além de  poupar recursos e gastos desnecessários.</p>', 'LIBERADO'),
(36, 'Relatório/Indivíduo/Com doses a vencer', 'Rel/exibirFormularioEscolherRelatorio', 'Indivíduos com doses a vencer', '<p>O relatório tem a finalidade de listar os indivíduos¹ que iniciaram o ciclo de vacinação e não concluíram. \r\n      É gerado  por estado, cidade e o período em que a dose se encontra.      </p>\r\n    <p>Para gerar o relatório selecione o estado e a cidade, informe o período e o nome² do indivíduo e utilize o botão <span class="campoFormulario">buscar</span> para  gerar. Abaixo será exibido o nome do indivíduo encontrado e que deverá ser clicado para visualizar a data em que a vacina deveria ter sido aplicada. </p>\r\n    <p align="center"><img src="../Imagens/Ajuda/vacinaDoseVencer.jpg" /></p>\r\n    <p>Caso, mesmo após conferir que o prazo ideal para a aplicação da vacina tenha sido ultrapassado, e deseja-se continuar o cliclo, clique sobre o nome da vacina, confirme na janela que deseja reiniciar o ciclo de vacinação e registre a aplicação da vacina clicando no botão <span class="campoFormulario">aplicar</span>, em forma de seringa. </p>\r\n    <p align="center"><img src="../Imagens/Ajuda/vacinaDoseVencer2.jpg" alt="dose vencer2" /></p>\r\n<p><strong>Nota:</strong>\r\n    <blockquote>¹ O administrador de acesso pardrão também é apto a realizar esta operação.</blockquote>\r\n    <blockquote>² O nome a ser buscado deve conter no mínimo 3 letras.\r\n    </blockquote>\r\n    </p>', 'RELATORIOS_INDIVIDUO'),
(39, 'Administração/Configurar/Nota', 'Adm/nota, Pop/detalhesVacina, Pop/notasDaVacina', 'Notas da vacina', '<p>Formulário reservado a procura e publicação de notas técnicas relacionadas   as   vacinas. Selecione a vacina desejada no campo vacinas, em seguida aparecerá uma lista  de notas. </p>\r\n<p align="center"><img src="../Imagens/Ajuda/visualizarnota.jpg\r\n" alt="Visualizar nota" /></p>\r\n<p>Selecione visualizar notas para ter acesso ao conteúdo.A publicação de notas é restrita ao administrador. Insira o título e o conteúdo então aperte <span class="campoFormulario">confirmar</span> para cadastrar a nota técnica e <span class="campoFormulario">limpar</span> para corrigir.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/notacadastrada.jpg\r\n" alt="Notas cadastradas" /></p>\r\n\r\n<p>Também é possível acessar notas técnicas através do formulário de vacinação. Clicando sobre o nome da vacina abrirá uma nova aba com suas informações. Em seguida click no botão no canto superior direito para acessar a relação de notas.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/explicacaonota.jpg\r\n" alt="Explicacaonota" /></p>\r\n<p><strong>Nota:</strong>\r\n<blockquote>¹ Somente o administrador de  acesso master está autorizado adicionar nova campanha.</blockquote>\r\n\r\n', 'CONFIGURAR_NOTA'),
(41, 'Relatório/Acesso e funcionalidade', 'Rel/listarRelatorios&subtipo=geral', 'Acesso e funcionalidade', '<p>Para acessar os relatórios é bem simples. No menu principal selecione relatórios e um sub-menu aparecerá.Agora selecione um dos itens para visualizar os relatórios referentes.</p> \r\n<p align="center"><img src="../Imagens/Ajuda/RelAcessoFuncionalidade.jpg" alt="Vacinar com data retroativa" /></p>\r\n<p>Aqui estão os relatórios referentes à escolha anterior. </p>\r\n<p align="center"><img src="../Imagens/Ajuda/RelAcessoFuncionalidade3.jpg" alt="Vacinar com data retroativa" /></p>\r\n<p>Também é possível exibir  uma lista  com todos os itens e seus respectivos relatórios,clicando diretamente em relatórios no menu principal.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/RelAcessoFuncionalidade2.jpg" alt="Vacinar com data retroativa" /></p>', 'RELATORIOS_'),
(42, 'Relatório/ACS/Indivíduos acamados', 'Rel/exibirFormularioEscolherRelatorio&tipo=PessoasComIntercorrenciaPorCidadeEVacina&tituloRelatorio=Usuários+com+eventos+adversos+ocorridos+por+vacina+e+cidade', 'Indivíduos acamados por ACS', '<p>O relatório tem a finalidade de listar os indivíduos acamados relacionados a um Agente Comunitário de Saúde. Basta selecionar Unidade de Saúde e Agente Comunitário de Saúde e utilizar <span class="campoFormulario">confirmar</span> para gerar o relatório.  </p>\r\n<p align="center"><img src="../Imagens/Ajuda/relIndvAcamAcs.JPG\r\n" alt="Acamados acs" /></p>\r\n', 'RELATORIOS_ACS'),
(43, 'Relatório/Cidade/Indivíduos acamados', 'Rel/exibirFormularioEscolherRelatorio&tipo=IndividuosAcamadosPorCidade&tituloRelatorio=Indivíduos+Acamados+por+cidade', 'Indivíduos acamados por cidade', '<p>O relatório tem a finalidade de listar os indivíduos acamados dentro de um determinado estado e cidade.Basta selecionar Estado e Cidade e utilizar <span class="campoFormulario">confirmar</span> para gerar o relatório. \r\n\r\n<p align="center"><img src="../Imagens/Ajuda/relCidAcamados.JPG\r\n" alt="Notas da vacina" /></p>\r\n', 'RELATORIOS_CIDADE'),
(44, 'Relatório/Unidade/Vacinados por faixa etária e unidade', 'Rel/exibirFormularioEscolherRelatorio&tipo=ListarUsuariosVacinadosPorFaixaEtariaVacinaEUnidade&tituloRelatorio=Vacinados+por+faixa+etária+e+unidade', 'Vacinados por faixa etária e unidade', '<p>O relatório tem a finalidade de listar, através do nome do indivíduo, as vacinas que foram tomadas dentro de uma faixa etária expressa em dias, semanas, meses ou anos.Selecione Unidade de Saúde e Vacina e insira as Faixas Etárias.Para produzir o relatório utilize <span class="campoFormulario">confirmar</span>.  </p>\r\n<p align="center"><img src="../Imagens/Ajuda/relUnidVacFaixaEtUnid.JPG\r\n" alt="Notas da vacina" /></p>\r\n', 'RELATORIOS_UNIDADE'),
(45, 'Relatório/Unidade/Quantidade de vacinados por faixa etária', 'Rel/exibirFormularioEscolherRelatorio&tipo=QuantidadeDeUsuariosVacinadosPorPeriodo&tituloRelatorio=Quantidade+de+vacinados+por+faixa+etária', 'Quantidade de vacinados por faixa etária', '<p>O relatório tem a finalidade de mostrar a quantidade de mulheres e homens vacinados por dose.Selecione Unidade de Saúde e Vacina.Insira Faixa etária inicial e Faixa etária final utilize <span class="campoFormulario">confirmar</span> para gerar o relatório.</p>\r\n\r\n<p align="center"><img src="../Imagens/Ajuda/relUnidQuantVacFaixEtaria.JPG\r\n" alt="" /></p>\r\n', 'RELATORIOS_UNIDADE'),
(46, 'Relatório/Unidade/Indivíduos a serem vacinados por período e unidade', 'Rel/exibirFormularioEscolherRelatorio&tipo=PessoasASeremVacinadasPorPeriodoEUnidade&tituloRelatorio=Indivíduos+a+serem+vacinados+por+período+e+unidade', 'Indivíduos a serem vacinados por período e unidade', '<p>Este relatório tem a função de fazer uma projeção dos indivíduos que serão vacinados dentro do período determinado.Baseando-se nos ciclos.Selecione Unidade de Saúde, Vacina e insira Data Início e Data Final.Para gerar o relatório utilize <span class="campoFormulario">confirmar</span>.</p> \r\n<p align="center"><img src="../Imagens/Ajuda/relUnidIndvSerVacPeriUnid.JPG\r\n" alt="" /></p>\r\n', 'RELATORIOS_UNIDADE'),
(47, 'Relatório/Unidade/Indivíduos acamados', 'Rel/exibirFormularioEscolherRelatorio&tipo=IndividuosAcamadosPorUnidade&tituloRelatorio=Indivíduos+Acamados+por+unidade', 'Indivíduos Acamados por unidade', '<p>O relatório tem a finalidade de listar os indivíduos acamados em cada unidade de saúde.Basta selecionar Unidade de Saúde e utilizar <span class="campoFormulario">confirmar</span> para gerar o relatório.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relUnidIndvAcamados.JPG\r\n" alt="" /></p>\r\n', 'RELATORIOS_UNIDADE'),
(48, 'Relatório/Intercorrências/Usuários com eventos adversos ocorridos por vacina e cidade', 'Rel/exibirFormularioEscolherRelatorio&tipo=PessoasComIntercorrenciaPorCidadeEVacina&tituloRelatorio=Usuários+com+eventos+adversos+ocorridos+por+vacina+e+cidade', 'Usuários com eventos adversos ocorridos por vacina e cidade', '<p>A finalidade deste relarório é listar os indivíduos que apresentaram alguma intercorrência após administração de uma determinada vacina.Selecione Estado, Cidade e Vacina e utilize <span class="campoFormulario">confirmar</span> para gerar o relatório.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/relIntercUsuIntercVacCid.JPG\r\n" alt="" /></p>\r\n', 'RELATORIOS_INTERCORRENCIA'),
(49, 'Administração/Estoque/Descrição geral', 'pagina=Adm/estoqueDaUnidade', 'Descrição geral', '<p>Esta parte é dedicada a todas as operações de controle de estoque. Selecionando uma das opções é possível iniciar uma das funções de gerenciamento. Tais como descartar vacinas, visualizar estoque e alimentar a unidade.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/estoque.JPG\r\n" alt="Descrição geral" /></p>\r\n', 'UNIDADES_ESTOQUE'),
(50, 'Administração/Estoque/Alimentar unidade central', 'pagina=Adm/alimentarUnidadeCentral', 'Alimentar unidade central', '<p>Selecione a cidade, a unidade central de saúde  e a vacina. Insira a quantidade, data de entrada, lote e validade do lote.Se uma observação for necessária ela também pode ser anexada e para concluir utilize <span class="campoFormulario">confirmar</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/alimEstoque.JPG" alt="" /></p>', 'UNIDADES_ESTOQUE_ALIMENTAR'),
(51, 'Administração/Estoque/Dispensar estoque para unidade satélite', 'pagina=Adm/dispensarParaUnidadeSatelite', 'Dispensar estoque para unidade satélite', '<p>Selecione a unidade central de saúde, a unidade de saúde  e a vacina.Insira a quantidade, data de envio, o lote e validade do lote.Se uma observação for necessária ela também pode ser anexada e para concluir utilize <span class="campoFormulario">confirmar</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/dispEstParUnidSat.JPG" alt="" /></p>', 'UNIDADES_ESTOQUE_DISPENSAR'),
(52, 'Administração/Estoque/Retornar estoque para unidade Central', 'pagina=Adm/retornarEstoqueParaCentral', 'Retornar estoque para unidade Central', '<p>Selecione a unidade central de saúde, a unidade de saúde e a vacina. Insira a quantidade, data de envio, lote e a validade do lote. Se uma observação for necessária ela também pode ser anexada e para concluir utilize <span class="campoFormulario">confirmar</span>.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/retEstUnidCentral.JPG" alt="" /></p>', 'UNIDADES_ESTOQUE_RETORNAR'),
(53, 'Administração/Estoque/Visualizar estoque das unidades dos municípios ', ' pagina=Adm/visualizarEstoqueUnidadesEstado', 'Visualizar estoque das unidades do Estado', '<p>Apresenta o relatório do estoque de uma determinada unidade dentro do estado. Selecione Cidade, Unidade de Saúde e Vacina. Para visualizar o relatório é só utilizar o <span class="campoFormulario">confirmar</span> após a seleção dos dados. </p><p align="center"><img src="../Imagens/Ajuda/visEstUnidMunic.JPG" alt="" /></p>', 'UNIDADES_ESTOQUE_VIZUALIZAR_ESTADO');
INSERT INTO `ajuda` (`id`, `indice`, `mapeamento`, `titulo`, `conteudo`, `permissao`) VALUES
(54, 'Administração/Estoque/Visualizar estoque de unidades do município', 'pagina=Adm/visualizarEstoqueUnidadesMunicipio', 'Visualizar estoque de unidades do município', '<p>Apresenta o relatório do estoque das unidades do município.Basta marcar esta operação de estoque que o relatório será gerado automaticamente.</p>\r\n<p align="center"><img src="../Imagens/Ajuda/visEstVascUnidMinic.JPG" alt="" /></p>', 'UNIDADES_ESTOQUE_VIZUALIZAR_MUNICIPIO'),
(55, 'Administração/Estoque/Vizualizar estoque da unidade', 'pagina=Adm/visualizarEstoqueUnidadesUnidade', 'Vizualizar estoque da unidade', '<p>Marcando esta operação de estoque automaticamente será gerado um relatório do estoque da unidade de saúde. \r\n</p>\r\n<p align="center"><img src="../Imagens/Ajuda/visEstVacUnidSaude.JPG" alt="" /></p>', 'UNIDADES_ESTOQUE_VIZUALIZAR_UNIDADE'),
(56, 'Administração/Estoque/Descartar vacina de unidade do Município', ' pagina=Adm/descartarVacinaMunicipio', 'Descartar vacina de unidade do Município', '<p>Selecione Unidade de Saúde, Vacina, Motivo do Descarte e Quantidade.Se for necessário é possível anexar uma observação.Para concluir o descarte utilize <span class="campoFormulario">confirmar</span>.\r\n\r\n</p><p align="center"><img src="../Imagens/Ajuda/descVascMunic.JPG" alt="Cadastrar Indivíduo" /></p>', 'UNIDADES_ESTOQUE_DESCARTAR_MUNICIPIO'),
(57, 'Administração/Estoque/Descartar vacina da unidade ', 'pagina=Adm/descartarVacinaUnidade', 'Descartar vacina da unidade ', '<p>Selecione Vacina, Motivo de Descarte e Quantidade. Se for necessário também é possível anexar uma observação. Para concluir o descarte utilize <span class="campoFormulario">confirmar</span>.\r\n\r\n  </p>\r\n\r\n<p align="center"><img src="../Imagens/Ajuda/descVascDestUnidSaude.jpg" alt="Descartar Vacina desta Unidade" /></p>', 'UNIDADES_ESTOQUE_DESCARTAR_UNIDADE');
